INSERT INTO `news` (`id`, `title`, `content`, `listImageUrl`, `infoImageUrl`, `date`, `sort`, `created_at`, `updated_at`) VALUES
	(1, '7月預購涼感衣延誤公告', '<p>公告</p><p>7/20預購的涼感衣，因國際物流受到疫情影響造成部分原料延誤進入國內，導致生產進度嚴重耽誤</p><p>故涼感衣將延至11/20發貨，為了表達對各位的歉意，凡是有購買涼感衣的客人，我們將賠償襪子一雙、零錢包一個，感謝各位長期對惡名昭彰的支持與愛護，造成各位的不便，我們深感抱歉</p>', '', NULL, '2020-10-20', 0, '2020-10-21 13:23:15', '2020-10-21 13:51:13'),
	(4, '10/20預購商品公告', NULL, NULL, '/upload/news/16032907780a09c8844ba8f0936c20bd791130d6b6.webp', '2020-10-20', 0, '2020-10-21 14:32:58', '2020-10-21 14:32:58'),
	(5, '調理包滿千免運！數量有限，要買要快！', '<p><span>﻿</span>10/20 20:00 冷凍調理包系列消費滿千免運！數量有限，要買要快！</p><p>參與調理包品項：洋蔥醬燒豬肉、法式黃芥末子豬膝燉馬鈴薯、三杯薑燒膀上肉、香辣牛筋、宮爆豬小骨、蒜香茄汁辣味雞、茄汁咖哩雞、紅燒獅子頭、綜合冷滷味、綜合熱滷味、古早味小魚乾、香滷鴨翅</p>', '/upload/news/16032912710aa1883c6411f7873cb83dacb17b0afc.webp', '/upload/news/1603291271084b6fbb10729ed4da8c3d3f5a3ae7c9.jpg', '2020-10-20', 0, '2020-10-21 14:41:11', '2020-10-21 14:41:11'),
	(6, '雲音響中獎須知，得獎人請詳細閱讀', '<p style="line-height: 1;">親愛的館粉們：</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">&nbsp; &nbsp; 恭喜您抽中劇院級跨界智慧雲音響，您不需負擔任何費用。但由於法規規定，中獎金額超過兩千元以上者，必須請消費者提供身分證影本以便年底扣繳申報所得。而本活動抽獎獎品價值遠超過新台幣兩千元，年底本公司會寄出免扣繳憑單，請中獎者明年五月申報所得稅時一併繳納。</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">&nbsp;</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">請得獎人必須提供以下的資料：</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">&nbsp;</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">(免扣繳憑單使用)</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">1.&nbsp; &nbsp; &nbsp; 身分證 正、反面影本</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">(要夾帶檔案傳出)</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">&nbsp;</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">(寄贈品時使用)</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">1.&nbsp; &nbsp; &nbsp; 姓名(不能是代號或英文名)</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">2.&nbsp; &nbsp; &nbsp; 收貨地址</p><p style="line-height: 1;">3.&nbsp; &nbsp; &nbsp; 電話</p><p style="line-height: 1;"><br></p><p style="line-height: 1;">請將以上資料寄至 智媒體科技股份有限公司活動信箱 kenix.kuo@smartmedia.com.tw，公司收到資料後會盡快將贈品寄出。</p>', NULL, NULL, '2020-10-12', 0, '2020-10-21 14:43:54', '2020-10-21 14:46:32'),
	(7, '《鬼滅之刃劇場版：無限列車篇》電影優惠券，10月10日現貨買就送！', '<p>2020/10/10 晚間 8 點後，凡於 【惡名昭彰】 官網購買 「現貨常溫商品」 之消費者，不限消費金額， 「隨機附贈」 《鬼滅之刃劇場版：無限列車篇》 電影優惠券 2 張。</p>', NULL, '/upload/news/16032916817ef605fc8dba5425d6965fbd4c8fbe1f.webp', '2020-10-08', 0, '2020-10-21 14:48:02', '2020-10-21 14:48:02'),
	(8, '特惠免運組，10/10(六) 20:00 限量開賣！', '<div>冷凍商品大回饋！</div><div><br></div><div>調理包十入組 + 冰淇淋綜合組，原價2600，特價2500；辣味九入組 + 冰淇淋綜合組，原價2475，特價2375。</div><div><br></div><div>除此之外，免！運！費！</div><div><br></div><div>數量有限！手刀購入！</div>', '/upload/news/1603292128c9e1074f5b3f9fc8ea15d152add07294.webp', '/upload/news/160329212884d9ee44e457ddef7f2c4f25dc8fa865.jpg', '2020-10-07', 0, '2020-10-21 14:55:28', '2020-10-21 14:55:28'),
	(9, '預購全反光連帽外套、拖鞋商品退款公告', '<p>凡預購全反光連帽外套、拖鞋商品</p><p>本公司將於即日起開始協助退款作業</p><p>退款方式如下</p><p><br></p><p>1、現金退款</p><p>&nbsp; &nbsp; 如需現金退款者，請於2020/10/8號前，於每日12:00~2100間</p><p>來電或於粉絲團聯繫客服，使用信用卡、ATM及超商代碼繳費，須提供存摺封面照片(需有清晰戶名、帳號，銀行名稱及分行名稱)</p><p>客服收到完整訊息後，將盡快協助處理退款作業，退款日期為2020/10/20&nbsp; 統一退款。</p><p><br></p><p>P.S. 如未在2020/10/8前，告知客服人員退款方式，本司將於10/10前將購物金退款至您購買的帳號內</p><p><span style="font-size: 0.9rem;"><br></span></p><p><span style="font-size: 0.9rem;">以上如有任何疑問，請於每日12:00~2100間，請來電客服詢問。</span></p>', NULL, NULL, '2020-10-06', 0, '2020-10-21 14:57:03', '2020-10-21 14:57:03'),
	(10, '經典限量機械錶，延誤出貨通知', '<p>經典限量機械錶，由於新冠肺炎疫情影響，導致瑞士機芯產能大幅下降。</p><p>原定9/28前出貨完畢，因遇10月份連續假期及瑞士機芯延遲交貨，時間將往後順延至9/28到10/20陸續完成出貨。</p><p><br></p><p>對於疫情的影響，我們對於延遲出貨深感抱歉，感謝您的耐心等候！</p>', '/upload/news/160329235402522a2b2726fb0a03bb19f2d8d9524d.webp', NULL, '2020-09-24', 0, '2020-10-21 14:58:06', '2020-10-21 14:59:14'),
	(11, '限時免運！9/20 20:00 - 9/30 23:59 調理包滿2000免運！', '<span>限時免運！9/20 20:00 - 9/30 23:59 調理包滿2000免運！</span>', '/upload/news/16032924741c9ac0159c94d8d0cbedc973445af2da.webp', '/upload/news/1603292474f2217062e9a397a1dca429e7d70bc6ca.jpg', '2020-09-18', 0, '2020-10-21 15:01:14', '2020-10-21 15:01:14'),
